import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = 'http://localhost:5000/api';

function App() {
  const [anuncios, setAnuncios] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('todos');
  const [anuncioSeleccionado, setAnuncioSeleccionado] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [nuevoAnuncio, setNuevoAnuncio] = useState({
    titulo: '',
    descripcion: '',
    categoria: 'evento',
    precio: '',
    ubicacion: '',
    contacto: '',
  });
  const [aiStatus, setAiStatus] = useState({ status: 'checking', method: '' });
  const [autoCategorizing, setAutoCategorizing] = useState(false);

  useEffect(() => {
    cargarAnuncios();
  }, [categoriaSeleccionada]);

  // Check AI status on mount
  useEffect(() => {
    axios.get(`${API_URL}/ai/status`)
      .then(res => setAiStatus(res.data))
      .catch(() => setAiStatus({ status: 'fallback', method: 'keywords' }));
  }, []);

  // Auto-categorize when user types
  useEffect(() => {
    if (nuevoAnuncio.titulo.length > 5 && nuevoAnuncio.descripcion.length > 15) {
      setAutoCategorizing(true);

      const timer = setTimeout(async () => {
        try {
          const response = await axios.post(`${API_URL}/anuncios/auto-categorize`, {
            titulo: nuevoAnuncio.titulo,
            descripcion: nuevoAnuncio.descripcion
          });

          setNuevoAnuncio(prev => ({
            ...prev,
            categoria: response.data.categoria
          }));
        } catch (error) {
          console.error('Error auto-categorizing:', error);
        }
        setAutoCategorizing(false);
      }, 1500); // Wait 1.5s after user stops typing

      return () => clearTimeout(timer);
    }
  }, [nuevoAnuncio.titulo, nuevoAnuncio.descripcion]);

  const cargarAnuncios = async () => {
    try {
      const response = await axios.get(`${API_URL}/anuncios`, {
        params: { categoria: categoriaSeleccionada }
      });
      setAnuncios(response.data);
    } catch (error) {
      console.error('Error al cargar anuncios:', error);
    }
  };

  const handleCrearAnuncio = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/anuncios`, nuevoAnuncio);
      setMostrarFormulario(false);
      setNuevoAnuncio({
        titulo: '',
        descripcion: '',
        categoria: 'evento',
        precio: '',
        ubicacion: '',
        contacto: '',
      });
      cargarAnuncios();
    } catch (error) {
      console.error('Error al crear anuncio:', error);
    }
  };

  const getCategoriaLabel = (categoria) => {
    const labels = {
      'evento': 'Events',
      'servicio': 'Services',
      'producto': 'Products/Rentals'
    };
    return labels[categoria] || categoria;
  };

  const getCategoriaColor = (categoria) => {
    const colors = {
      'evento': '#13c2c2',
      'servicio': '#52c41a',
      'producto': '#fa8c16'
